import { Box, Divider, Heading, Text, Image, Link } from "@chakra-ui/react";
import { useIntl } from "react-intl";
import { useHistory } from "react-router";

const Option = (prop) => {
  const { header, title, msg, illustrators, link } = prop
  const hist = useHistory()
  const intl = useIntl()
  return (
    <Box className="adding-option-container" >
  {/*     <Heading className="heading" size="md">{intl.formatMessage(header)}</Heading> */}
      <Box className="option-wrapper" onClick={() => {
        hist.push(link)
      }}>
        {/* <Heading style={{ marginBottom: "10px" }} size="md">{intl.formatMessage(title)}</Heading> */}
        <Box mb='auto' textStyle='normalBold'>{intl.formatMessage(title)}</Box>
        <Image width="30%" height="auto" src={illustrators} />
        <Box mt='auto'>
        <Text>{intl.formatMessage(msg)}</Text>
        </Box>
      </Box>
    </Box>
  );
}
export default Option;
