import React, {forwardRef} from 'react';

import {
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalCloseButton,
    ModalBody,
    ModalFooter,
    Button,
    Box
 } from '@chakra-ui/react'



const  SimpleModal = forwardRef(({hidefooter, size, onClose,isOpen, children, okText, onOk, title, isLoading}, ref) =>  {

    return (
        <Modal 
          isCentered motionPreset='slideInBottom' 
          size={size ?? 'sm'} 
          isOpen={isOpen} 
          onClose={onClose}
          scrollBehavior='inside'
          
          >
          <ModalOverlay />
          <ModalContent>
            <ModalHeader>{title}</ModalHeader>
            <ModalCloseButton onClick={onClose}/>
       
            <ModalBody >
                {children}
            </ModalBody>

            {!hidefooter && <ModalFooter>
              <Button variant='ghost' mr={3} onClick={onClose}>
                Close
              </Button>
              <Button ref={ref} isLoading={isLoading} variant='normal' onClick={onOk}>{okText}</Button>
            </ModalFooter>}
          </ModalContent>
        </Modal>
    )
  })

  export default SimpleModal;
