import React, { forwardRef } from 'react';

import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
  ModalFooter,
  Button,
  Box,
} from '@chakra-ui/react';

const SimpleModal = forwardRef(
  (
    {
      hidefooter,
      size,
      onClose,
      isOpen,
      children,
      okText,
      onOk,
      title,
      isLoading,
      isModalBody,
    },
    ref,
  ) => {
    return (
      <Modal
        isCentered
        motionPreset='slideInBottom'
        size={size ?? 'sm'}
        isOpen={isOpen}
        onClose={onClose}
        scrollBehavior='inside'
      >
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>{title}</ModalHeader>
          <ModalCloseButton onClick={onClose} />

          {!isModalBody ? (
            <ModalBody>{children}</ModalBody>
          ) : (
            <Box p={5}>{children}</Box>
          )}

          {!hidefooter && (
            <ModalFooter>
              <Button variant='ghost' mr={3} onClick={onClose}>
                Close
              </Button>
              <Button
                ref={ref}
                isLoading={isLoading}
                variant='normal'
                onClick={onOk}
              >
                {okText}
              </Button>
            </ModalFooter>
          )}
        </ModalContent>
      </Modal>
    );
  },
);

export default SimpleModal;
